import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { environment } from 'src/environments/environment';
import { Medicine } from '../_models/medicine';
import { Patient } from '../_models/Patient';
import { Doctor } from '../_models/Doctor';

@Injectable({
  providedIn: 'root'
})
export class DataService {

  private url!: string;

  constructor(private httpClient: HttpClient) {
    this.url = `${environment.apiUrl}/Data`;
  }

  getMedicines(): Observable<Medicine[]> {
    let fullUrl = this.url + '/medicines';
    return this.httpClient.get<Medicine[]>(this.url, { responseType: 'json' });
  }

  getDoctors(): Observable<Doctor[]> {
    let fullUrl = this.url + '/doctors';
    return this.httpClient.get<Doctor[]>(this.url, { responseType: 'json' });
  }

  getPatients(): Observable<Patient[]> {
    let fullUrl = this.url + '/patients';
    return this.httpClient.get<Patient[]>(this.fullUrl, { responseType: 'json' });
  }


}
