import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, FormArray, Validators } from '@angular/forms';
import { Router, ActivatedRoute } from '@angular/router';
import { AppointmentService } from '../_services/appointment.service';
import { Observable, Subscription } from 'rxjs';
import { PatientDTO } from '../_models/patientDTO';
import { DoctorDTO } from '../_models/doctorDTO';
import { DataService } from '../_services/data.service';
import { AppointmentDTO } from '../_models/appointmentDTO';

@Component({
  selector: 'app-appointment-form',
  templateUrl: './appointment-form.component.html',
  styleUrls: ['./appointment-form.component.css']
})
export class AppointmentFormComponent implements OnInit {
  appointmentForm: FormGroup;
  isEditMode = false;
  appointmentId: number | null = null;
  public allPatients!: Observable<PatientDTO[]>;
  public allDoctors!: Observable<DoctorDTO[]>;

  constructor(
    private fb: FormBuilder,
    public appointmentService: AppointmentService,
    public dataService: DataService,
    private router: Router,
    private route: ActivatedRoute
  ) {
    this.appointmentForm = this.createForm();
  }

  ngOnInit(): void {
    this.allDoctors = this.dataService.getDoctors();
    this.allPatients = this.dataService.getPatients();

    this.route.params.subscribe(params => {
      if (params['id']) {
        this.isEditMode = true;
        this.appointmentId = Number(params['id']);
        this.loadAppointment(this.appointmentId);
      }
    });
  }

  createForm(): FormGroup {
    return this.fb.group({
      patientId: ['', Validators.required],
      doctorId: ['', Validators.required],
      appointmentDate: ['', Validators.required],
      visitType: ['', Validators.required],
      notes: ['', Validators.required],
      diagnosis: ['', Validators.required]
    });
  }

  loadAppointment(id: number): void {
    this.appointmentService.getAppointmentById(id).subscribe(
      (appointment: AppointmentDTO) => {
        if (appointment) {
          this.appointmentForm.patchValue({
            id: appointment.id,
            patientId: appointment.patientId,
            patientName: appointment.patientName,
            doctorId: appointment.doctorId,
            doctorName: appointment.doctorName,
            appointmentDate: appointment.appointmentDate.split('T')[0],
            visitType: appointment.visitType,
            notes: appointment.notes,
            diagnosis: appointment.diagnosis
          });
        }
      },
      (e) => {
        console.log('Error: ' + e);
      }
    );
  }

  onSubmit(): void {
    if (this.appointmentForm.valid) {
      const formValue : AppointmentDTO = this.appointmentForm.value;

      formValue.appointmentDate = new Date().getUTCDate;

      // formValue.appointmentDate = new Date(this.appointmentForm.value.appointmentDate).toISOString();

      if (this.isEditMode && this.appointmentId) {
        this.appointmentService.updateAppointment(this.appointmentId, formValue).subscribe(
          (r) => {
            this.router.navigate(['/appointment-list']);
          },
          (e) => {
            console.log(e);
          }
        )
      }
      else {
        this.appointmentService.createAppointment(formValue).subscribe(
          (r) => {
            this.router.navigate(['/appointment-list']);
          },
          (e) => {
            console.log(e);
          }
        )
      }

    }
  }

  cancel(): void {
    this.router.navigate(['/appointment-list']);
  }

  isFieldInvalid(fieldName: string): boolean {
    const field = this.appointmentForm.get(fieldName);
    return !!(field && field.invalid && (field.dirty || field.touched));
  }
}
<div class="row mt-4 justify-content-center">
  <div class="col-lg-8">
    <div class="card">
      <div class="card-header">
        <h5 class="mb-0">
          <i class="bi bi-plus-circle"></i>
          {{ isEditMode ? 'Edit Appointment' : 'Add New Appointment' }}
        </h5>
      </div>
      <div class="card-body">
        <form [formGroup]="appointmentForm" (ngSubmit)="onSubmit()">
          <!-- Master Form Fields -->
          <div class="row mb-3">
            <div class="col-md-6">
              <label class="form-label">Patient *</label>
              <select class="form-select" formControlName="patientId" [class.is-invalid]="isFieldInvalid('patientId')">
                <option value="">Select Patient</option>
                <option *ngFor="let patient of allPatients | async " [value]="patient.id">
                  {{ patient.name }}
                </option>
              </select>
              <div class="invalid-feedback" *ngIf="isFieldInvalid('patientId')">
                Please select a patient.
              </div>
            </div>
            <div class="col-md-6">
              <label class="form-label">Doctor *</label>
              <select class="form-select" formControlName="doctorId" [class.is-invalid]="isFieldInvalid('doctorId')">
                <option value="">Select Doctor</option>
                <option *ngFor="let doctor of allDoctors | async " [value]="doctor.id">
                  {{ doctor.name }}
                </option>
              </select>
              <div class="invalid-feedback" *ngIf="isFieldInvalid('doctorId')">
                Please select a doctor.
              </div>
            </div>
          </div>

          <div class="row mb-3">
            <div class="col-md-6">
              <label class="form-label">Appointment Date *</label>
              <input type="date" class="form-control" formControlName="appointmentDate"
                [class.is-invalid]="isFieldInvalid('appointmentDate')">
              <div class="invalid-feedback" *ngIf="isFieldInvalid('appointmentDate')">
                Please select a date.
              </div>
            </div>
            <div class="col-md-6">
              <label class="form-label">Visit Type *</label>
              <div class="mt-2">
                <div class="form-check form-check-inline visit-type-radio">
                  <input class="form-check-input" type="radio" formControlName="visitType" id="firstVisit"
                    value="First Visit">
                  <label class="form-check-label" for="firstVisit">First Visit</label>
                </div>
                <div class="form-check form-check-inline visit-type-radio">
                  <input class="form-check-input" type="radio" formControlName="visitType" id="followUp"
                    value="Follow-up">
                  <label class="form-check-label" for="followUp">Follow-up</label>
                </div>
              </div>
              <div class="text-danger small" *ngIf="isFieldInvalid('visitType')">
                Please select a visit type.
              </div>
            </div>
          </div>

          <div class="row mb-3">
            <div class="col-md-6">
              <label class="form-label">Notes</label>
              <input class="form-control" formControlName="notes"
                placeholder="Enter appointment notes"/>
                              <div class="text-danger small" *ngIf="isFieldInvalid('notes')">
                Please select a visit type.
              </div>
            </div>
            <div class="col-md-6">
              <label class="form-label">Diagnosis</label>
              <textarea class="form-control" formControlName="diagnosis" rows="3"
                placeholder="Enter diagnosis"></textarea>
                              <div class="text-danger small" *ngIf="isFieldInvalid('diagnosis')">
                Please select a visit type.
              </div>
            </div>
          </div>

          <!-- Form Actions -->
          <div class="d-flex justify-content-end mt-4">
            <button type="button" class="btn btn-secondary me-2" (click)="cancel()">
              <i class="bi bi-x-circle"></i> Cancel
            </button>
            <button type="submit" class="btn btn-primary" [disabled]="appointmentForm.invalid">
              <i class="bi bi-check-circle"></i> Save Appointment
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>

